class SubscriptionBloc extends Bloc<SubscriptionEvent, SubscriptionState> {SubscriptionBloc(SubscriptionState initialState) : super(initialState) { on<SubscriptionInitialEvent>(_onInitialize); }

_onInitialize(SubscriptionInitialEvent event, Emitter<SubscriptionState> emit, ) async  { emit(state.copyWith(subscriptionModelObj: state.subscriptionModelObj?.copyWith(poultrymarketItemList: fillPoultrymarketItemList()))); } 
